function AppExampleUseState () {
    const [job, setJob] = useState("")
    const [jobs, setJobs] = useState([]);


    const handleSetJobs = () => {
        setJobs(() => {
            return [...jobs, job]
        });
    }

    const clearJob = (nameJob) => {
        setJobs(() => {
            return jobs.filter(item => item !== nameJob)
        });
    }

    return (
        <div style={{padding: 32}}>
            <div>
                <input value={job} />
                <button onClick={handleSetJobs}>clear</button>

            </div>
            {
                jobs.map((j, index) => (<div key={index}>
                    <h1>{j}</h1>
                    <button onClick={clearJob(j)}>clear</button>
                </div>))
            }
        </div>
    );
}
export default AppExampleUseState;
