import { useState } from "react";

function AppUseSate() {

  const [counter, setHandle] = useState(1);

  const handleUpdate = () => {
    setHandle(counter + 1);
    setHandle(counter + 1);
    setHandle(counter + 1);
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>{counter}</h1>
        <button onClick={handleUpdate}>Increase</button>
      </header>
    </div>
  );
}

export default AppUseSate;
